import styled from "styled-components";

export const Container = styled.header`
  position: relative;
  height: 100px;
  font-size: 24px;
  font-weight: 700;

  display: flex;


`;
export const PokemonLogo = styled.img`
min-height: 15vh;
min-height: 20vh;

min-width: 10vw;
max-width: 10vw;
`;





export const Button = styled.button`
font-size: 17px;
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
width: 30vh;
height: 7vh;
background: #007FFF;
border-radius: 8px;
border: none;
cursor: pointer;
position: absolute;
left: 100%;
;
z-index: 0;
color: white;
`;
