import styled from "styled-components";

export const Container = styled.header`
  position: relative;
  height: 100px;
  font-size: 24px;
  font-weight: 700;

  display: flex;
  align-items: center;
  justify-content: center;

  button {
    position: absolute;
    left: 0;
    height: 30px;
    padding: 0 4px;
  }
  
`;
export const PokemonLogo = styled.img`
min-height: 10vh;
max-height: 1px;
min-width: 10vh;
max-width: 1vh;
`;





export const Button = styled.button`
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
padding: 4px 10px;
width: 146px;
height: 10px;
background: blue;
border-radius: 8px;
border: none;
cursor: pointer;
position: absolute;
bottom: 40px;

z-index: 2;
color: white;
`;
