import styled from "styled-components";

export const Container = styled.header`
  position: abosulte;
  height: 20%;
  width: 60%;
  font-size: 24px;

  display: flex;
  align-items: center;
  justify-content: center;
  
`;
export const PokemonLogo = styled.img`
min-height: 15vh;
min-height: 20vh;

min-width: 10vw;
max-width: 10vw;
`;





export const Button = styled.button`
font-size: 17px;
display: grid;
flex-direction: row;
justify-content: center;
align-items: center;
padding: 4px 10px;
width: 30vh;
height: 7vh;
background: #007FFF;
border-radius: 8px;
border: none;
cursor: pointer;
position: relative;
bottom: 0px;
left: 100%;

z-index: 1;
color: white;
`;
