import styled from "styled-components";

export const Container = styled.header`
position: relative;
height: 20%;

font-size: 24px;
font-weight: 700;
background: white;
display: flex;

z-index: 1;
`;
export const PokemonLogo = styled.img`
min-height: 15vh;
min-height: 20vh;

min-width: 10vw;
max-width: 10vw;
`;





export const Button = styled.button`
font-size: 17px;
display: grid;
flex-direction: row;
justify-content: center;
align-items: center;
padding: 4px 10px;
width: 30vh;
height: 7vh;
background: #007FFF;
border-radius: 8px;
border: none;
cursor: pointer;
position: relative;
bottom: 0 auto;
rigth: auto;

z-index: 0;
color: white;
`;
