import styled from "styled-components";

export const Container = styled.header`
  position: relative;
  height: 70px;
  font-size: 24px;
  font-weight: 700;

  display: flex;
  align-items: center;
  justify-content: center;

  button {
    position: absolute;
    left: 0;
    height: 30px;
    padding: 0 4px;
  }
  
`;
export const PokemonLogo = styled.img`
width: 500px;
height: 50px;
position: absolute;
display: center;

`;
