import styled from "styled-components";

export const Container = styled.header`
  position: relative;
  height: 70px;
  font-size: 24px;
  font-weight: 700;

  display: flex;
  align-items: center;
  justify-content: center;

  button {
    position: absolute;
    left: 0;
    height: 30px;
    padding: 0 4px;
  }
  
`;
export const PokemonLogo = styled.img`
min-height: 1vh;
max-height: 1px;
min-width: 1vh;
max-width: 1vh;
`;
export const PokemonHeaderContainer = styled.div`
padding: 16px;
min-height: 20vh;
max-height: 50px;
min-width: 185vh;
max-width: 200vh;
background-color: white;
border-radius: 12px;
display: flex;
position: relative;
margin: 50px;
color: #ffffff;



`;
export const Button = styled.button`
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
padding: 4px 10px;
width: 146px;
height: 38px;
background: #ffffff;
border-radius: 8px;
border: none;
cursor: pointer;
position: absolute;
bottom: 30px;

z-index: 1;
color: #000;
`;
