import { useLocation, useNavigate } from "react-router-dom";
import { goToHomePage, goToPokedexPage } from "../../routes/coordinator";
import { Container } from "./styled";
import { PokemonLogo,Button } from "./styled";
function Header() {
  // hook para saber nosso path atual
  const location = useLocation();

  // hook para redirecionar
  const navigate = useNavigate();

  const url ="https://th.bing.com/th/id/R.831598d90c212654b9576578a73f821c?rik=yI6cXEPfc4o9%2bg&riu=http%3a%2f%2fclipart-library.com%2fimages%2f8TEb45kEc.png&ehk=urfrtuGp6GEpJHMl%2fg9nPY3yFhTSQSr21%2bUCGtOJ2bg%3d&risl=&pid=ImgRaw&r=0"
  const renderHeader = () => {
    switch (location.pathname) {
      case "/":
        return (
         <>
            <Button onClick={() => goToPokedexPage(navigate)}>
              Ver pokedex
            </Button>
            <PokemonLogo src={`${url}`} alt="" />
        
            </>
            
        );
      case "/pokedex":
        return (
          <>
            <button onClick={() => goToHomePage(navigate)}>
              Ver lista de pokemons
            </button>
            <span>Pokedex</span>
          </>
        );
      default:
        return (
          <>
            <button onClick={() => goToHomePage(navigate)}>
              Voltar para página inicial
            </button>
            <span>Página inexistente</span>
          </>
        );
    }
  };

  return <Container>{renderHeader()}</Container>;
}

export default Header;
