import styled from "styled-components";

export const Container = styled.header`
  position: relative;
  height: 100px;
  width: 170vh;
  font-size: 24px;
  font-weight: 700;

  display: flex;
  align-items: center;
  justify-content: center;

  
  
`;
export const PokemonLogo = styled.img`
min-height: 12vh;
max-height: 25vh;

min-width: 1vw;
max-width: 10vw;
`;





export const Button = styled.button`
font-size: 17px;
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
padding: 4px 10px;
width: 30vh;
height: 7vh;
background: #007FFF;
border-radius: 8px;
border: none;
cursor: pointer;
position: absolute;
bottom: 20px;
right: 22px;

z-index: 2;
color: white;
`;
