import styled from "styled-components";

export const P = styled.p`
display: grid;
flex-direction: row;
justify-content: center;
min-height: 15vh;
min-height: 20vh;
min-width: 15vw;
max-width: 15vw;
right: 15%;
bottom: -100px;
position: relative;


`

export const Container = styled.header`
position: relative;
height: 100px;
font-size: 24px;
font-weight: 700;

display: flex;
align-items: center;
justify-content: center;
`;
export const PokemonLogo = styled.img`
display: grid;
flex-direction: row;
justify-content: center;
min-height: 15vh;
min-height: 20vh;
min-width: 15vw;
max-width: 15vw;
right: 8%;
position: relative;
`;





export const Button = styled.button`
font-size: 17px;
display: grid;
flex-direction: row;
justify-content: center;
align-items: center;
padding: 4px 10px;
width: 30vh;
height: 7vh;
background: #007FFF;
border-radius: 8px;
border: none;
cursor: pointer;
position: relative;
bottom: 0;
left: 49%;

color: white;
`;
