import styled from "styled-components";


export const P = styled.p`
display: grid;
flex-direction: row;
justify-content: center;
min-height: 15vh;
min-height: 25vh;
min-width: 15vw;
max-width: 20vw;
left: 2%;
bottom: -50px;
position: relative;
`;

export const Container = styled.div`
  section {
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-gap: 8px;
  }
`;


export const H = styled.h1`
position: relative;
min-height: 200px;
min-width: 1320px;
font-size: 30px;
font-weight: 700;
background: grey;
display: flex;
align-items: center;
justify-content: left;
color: white;
`;
