import styled from "styled-components";

export const Container = styled.header`
position: relative;
height: 70px;
font-size: 24px;
font-weight: 700;

display: flex;
align-items: center;
justify-content: center;
  
  
`;
export const PokemonLogo = styled.img`
min-height: 12vh;
max-height: 25vh;
min-width: 15vw;
max-width: 10vw;

align-items: center;
justify-content: center;
`;





export const Button = styled.button`
position: absolute;
left: 0;
height: 30px;
padding: 0 4px;
`;
