import styled from "styled-components";

export const Container = styled.header`
  position: relative;
  height: 70px;
  font-size: 24px;
  font-weight: 700;

  display: flex;
  align-items: center;
  justify-content: center;

  button {
    position: absolute;
    left: 0;
    height: 30px;
    padding: 0 4px;
  }
  
`;
export const PokemonLogo = styled.img`

`;
export const PokemonHeaderContainer = styled.div`
padding: 16px;
min-height: 20vh;
max-height: 50px;
min-width: 185vh;
max-width: 200vh;
background-color: white;
border-radius: 12px;
display: flex;
position: relative;
margin: 50px;
color: #ffffff;



`;
export const Button = styled.button`
width: 100px;
height: 100px;
position: absolute;
top: 10px;
right: 0;
z-index: 2;

`;
