import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { goToDetailsPage } from "../../routes/coordinator";
import { Container,PokemonNumber,PokemonName,Pokemon,CatchButton,TypesContainer,PokemonType} from "./styled";
import {getTypes} from '../../utils/ReturnPokemonType'
import pokeball from '../../assets/'

function Card(props) {
  const { pokemonUrl, addToPokedex, removeFromPokedex } = props;

  // hook para saber nosso path atual
  const location = useLocation();

  // hook para redirecionar
  const navigate = useNavigate();

  const [pokemon, setPokemon] = useState({});

  // guarda, porque ainda não renderizamos
  useEffect(() => {
    fetchPokemon();
  }, []);

  const fetchPokemon = async () => {
    try {
      const response = await axios.get(pokemonUrl);
      setPokemon(response.data);
    } catch (error) {
      console.log("Erro ao buscar lista de pokemons");
      console.log(error);
    }
  };

  return (
    <Container color={props.cardColor}>
      <div>
        <PokemonNumber>{props.pokemon.id}</PokemonNumber>
        <PokemonName>{props.pokemon.name.english}</PokemonName>
        <TypesContainer>
            {props.pokemon.type.map((type) => {
                return <PokemonType key={type} src={getTypes(type)} alt='' />
            })}
        </TypesContainer>
        <p>Detalhes</p>
      </div>
      <div>
        <Pokemon src={`https://www.serebii.net/swordshield/pokemon/${props.pokemon.id}.png`} alt="" />
        <CatchButton>Capturar!</CatchButton>
      </div>
      <Pokeball src={pokeball} alt="pokeball" />
    </Container>
  );
}

export default Card;
